import Transport from '../Transport/index.mjs'

function ready() {
  return Transport.send('metrics', 'ready', {})
}

function signIn() {
  return Transport.send('metrics', 'signIn', {})
}

function signOut() {
  return Transport.send('metrics', 'signOut', {})
}

// Methods

function action(category, type, parameters) {
  const transforms = null

  return Transport.send(
    'Metrics',
    'action',
    { category, type, parameters },
    transforms,
  )
}

function appInfo(build) {
  const transforms = null

  return Transport.send('Metrics', 'appInfo', { build }, transforms)
}

function error(type, code, description, visible, parameters) {
  const transforms = null

  return Transport.send(
    'Metrics',
    'error',
    { type, code, description, visible, parameters },
    transforms,
  )
}

